import React from 'react';
import { GameResult } from '../App';

interface Scene5Props {
  result: GameResult | null;
  onReset: () => void;
}

const Scene5: React.FC<Scene5Props> = ({ result, onReset }) => {
  if (!result || !result.isWin) return null;

  // Ïø†Ìè∞ ÏΩîÎìú ÏÉùÏÑ± (ÌòÑÏû¨ ÎÇ†Ïßú Í∏∞Î∞ò)
  const generateCouponCode = () => {
    const date = new Date();
    const dateString = date.toISOString().slice(0, 10).replace(/-/g, '');
    const randomPart = Math.random().toString(36).substring(2, 8).toUpperCase();
    return `AKI${dateString}${randomPart}`;
  };

  // Ïú†Ìö®Í∏∞Í∞Ñ Í≥ÑÏÇ∞ (1Í∞úÏõî ÌõÑ)
  const getExpiryDate = () => {
    const date = new Date();
    date.setMonth(date.getMonth() + 1);
    return date.toLocaleDateString('ko-KR');
  };

  const couponCode = generateCouponCode();
  const expiryDate = getExpiryDate();

  // Ïø†Ìè∞ Ïù¥ÎØ∏ÏßÄ Ï†ÄÏû• Ìï®Ïàò
  const handleSaveCoupon = () => {
    // Ïø†Ìè∞ Ïù¥ÎØ∏ÏßÄ ÏÉùÏÑ±ÏùÑ ÏúÑÌïú Canvas
    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');
    
    if (!ctx) return;
    
    // Ïø†Ìè∞ ÌÅ¨Í∏∞ ÏÑ§Ï†ï
    canvas.width = 800;
    canvas.height = 500;
    
    // Î∞∞Í≤Ω Í∑∏ÎùºÎîîÏñ∏Ìä∏
    const gradient = ctx.createLinearGradient(0, 0, canvas.width, canvas.height);
    gradient.addColorStop(0, '#a8edea');
    gradient.addColorStop(1, '#fed6e3');
    ctx.fillStyle = gradient;
    ctx.fillRect(0, 0, canvas.width, canvas.height);
    
    // Ïø†Ìè∞ ÌÖåÎëêÎ¶¨
    ctx.strokeStyle = '#ff6b6b';
    ctx.lineWidth = 8;
    ctx.setLineDash([20, 10]);
    ctx.strokeRect(20, 20, canvas.width - 40, canvas.height - 40);
    
    // ÎÇ¥Î∂Ä Ìù∞ÏÉâ Î∞∞Í≤Ω
    ctx.fillStyle = '#ffffff';
    ctx.fillRect(40, 40, canvas.width - 80, canvas.height - 80);
    
    // ÌÖçÏä§Ìä∏ Ïä§ÌÉÄÏùº ÏÑ§Ï†ï
    ctx.textAlign = 'center';
    ctx.shadowColor = 'rgba(0, 0, 0, 0.3)';
    ctx.shadowBlur = 3;
    ctx.shadowOffsetX = 2;
    ctx.shadowOffsetY = 2;
    
    // Ï†úÎ™©
    ctx.font = 'bold 36px "Noto Sans KR", sans-serif';
    ctx.fillStyle = '#333333';
    ctx.fillText('üéÅ ÏïÑÌÇ§ÏïÑÌÇ§ ÎãπÏ≤® Ïø†Ìè∞', canvas.width / 2, 120);
    
    // ÏÉÅÌíàÎ™Ö
    ctx.font = 'bold 42px "Noto Sans KR", sans-serif';
    ctx.fillStyle = '#ff6b6b';
    ctx.fillText(result.prize, canvas.width / 2, 180);
    
    // Ïø†Ìè∞ ÏΩîÎìú Î∞∞Í≤Ω
    ctx.fillStyle = '#ff6b6b';
    ctx.fillRect(150, 210, 500, 60);
    
    // Ïø†Ìè∞ ÏΩîÎìú
    ctx.font = 'bold 28px "Courier New", monospace';
    ctx.fillStyle = '#ffffff';
    ctx.fillText(couponCode, canvas.width / 2, 250);
    
    // Ïú†Ìö®Í∏∞Í∞Ñ
    ctx.font = '20px "Noto Sans KR", sans-serif';
    ctx.fillStyle = '#333333';
    ctx.shadowBlur = 0;
    ctx.fillText(`Ïú†Ìö®Í∏∞Í∞Ñ: ${expiryDate}ÍπåÏßÄ`, canvas.width / 2, 320);
    
    // ÏÇ¨Ïö©Î≤ï
    ctx.font = '18px "Noto Sans KR", sans-serif';
    ctx.fillText('ÏÇ¨Ïö©Î≤ï: Îß§Ïû• Î∞©Î¨∏Ïãú Ïù¥ ÏΩîÎìúÎ•º Ï†úÏãúÌï¥Ï£ºÏÑ∏Ïöî', canvas.width / 2, 350);
    
    // ÌïòÎã® Î©îÏãúÏßÄ
    ctx.font = '16px "Noto Sans KR", sans-serif';
    ctx.fillStyle = '#666666';
    ctx.fillText('ÏïÑÌÇ§ÏïÑÌÇ§Îäî Í≥†Í∞ùÎãòÏùò Îçî Ï¢ãÏùÄ Í≤ΩÌóòÏùÑ ÏúÑÌïòÏó¨, Ìï≠ÏÉÅ ÏµúÏÑ†ÏùÑ Îã§ÌïòÍ≤†ÏäµÎãàÎã§.', canvas.width / 2, 420);
    
    // Ïù¥ÎØ∏ÏßÄ Îã§Ïö¥Î°úÎìú
    const link = document.createElement('a');
    link.download = `ÏïÑÌÇ§ÏïÑÌÇ§_Ïø†Ìè∞_${couponCode}.png`;
    link.href = canvas.toDataURL();
    link.click();
  };

  return (
    <div className="scene" style={{ 
      background: 'linear-gradient(135deg, #a8edea 0%, #fed6e3 100%)',
      minHeight: '100vh',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      padding: '2rem'
    }}>
      <div style={{
        animation: 'fadeInUp 1s ease-out',
        maxWidth: '600px',
        width: '100%',
        textAlign: 'center'
      }}>
        <h1 className="scene-title" style={{
          color: '#333',
          marginBottom: '2rem',
          textAlign: 'center'
        }}>
          Ï∂ïÌïòÌï©ÎãàÎã§. Ïø†Ìè∞ÏùÑ Î∞õÏúºÏÑ∏Ïöî!
